package com.amir.weathora.data.Repositoryimport com.amir.weathora.data.WeatherApiimport com.amir.weathora.data.mappers.toWeatherInfoimport com.amir.weathora.domain.repository.WeatherRepositoryimport com.amir.weathora.domain.util.Resourceimport com.amir.weathora.domain.weather.WeatherInfoimport javax.inject.Injectclass WeatherRepositoryImpl @Inject constructor(private val api: WeatherApi) : WeatherRepository {    override suspend fun getWeatherData(        lat: Double,        long: Double    ): Resource<WeatherInfo> {        return try {            Resource.Success(                data = api.getWeatherData(lat = lat, long = long).toWeatherInfo()            )        } catch (e: Exception) {            e.printStackTrace()            Resource.Error(e.message ?: "An unknown error occurred.")        }    }}