package com.amir.weathora.presentaionimport android.content.Contextimport androidx.core.content.editclass CityPreference(context: Context) {    private val prefs = context.getSharedPreferences("weather_prefs", Context.MODE_PRIVATE)    fun saveCity(city: String) {        prefs.edit { putString("city", city) }    }    fun getCity(): String? {        return prefs.getString("city", null)    }}