package com.amir.weathora.presentaionimport androidx.compose.runtime.Composableimport androidx.compose.runtime.LaunchedEffectimport androidx.compose.runtime.mutableStateOfimport androidx.compose.runtime.rememberimport kotlinx.coroutines.delayimport java.text.SimpleDateFormatimport java.util.Dateimport java.util.Localeimport java.util.TimeZone@Composablefun IranClock(): String {    val time = remember { mutableStateOf("") }    LaunchedEffect(Unit) {        while (true) {            val sdf = SimpleDateFormat("HH:mm:ss", Locale("fa", "IR"))            sdf.timeZone = TimeZone.getTimeZone("Asia/Tehran")            time.value = sdf.format(Date())            delay(1000)        }    }    return time.value}