package com.amir.weathora.presentaionfun getMessageForTemperature(temp: Float): String {    return when {        temp < 0 -> "Brrr... it's freezing! 🥶"        temp in 0.0..10.0 -> "Quite cold outside, stay warm! 🧥"        temp in 10.0..25.0 -> "Perfect weather for a walk 🌤️"        temp in 25.0..35.0 -> "Getting warm! Stay hydrated ☀️"        else -> "It's hot! Better stay inside 🥵"    }}